import { createStore } from 'vuex'

export default createStore({
  state: {
    counter: 0,
    firstName: ""
  },
  getters: {
  },
  mutations: {
    decrement(state) {
      state.counter--
  },
    increment(state) {
      state.counter++
  },
    saveFirstname(state, payload) {
      state.firstName = payload
    }
  },
  actions: { //dispatch
    saveFirstName(context, payload) { 
       context.commit('saveFirstname', payload)
    }
  },
  modules: {
  }
})
