import { createStore } from 'vuex'

export default createStore({
  state: {
    token: "",
    acesso: "",
    logado: false
  },
  getters: {
  },
  mutations: { //commit
    saveToken(state, payload) {
      state.token = payload
    },
    saveAcesso(context, payload) { 
      state.acesso = payload
    },
   saveLogado(context, payload) { 
    context.commit('saveLogado', payload)
    }

  },
  actions: { //dispatch
    saveToken(context, token) { 
       context.commit('saveToken', payload)
    },
    saveAcesso(context, payload) { 
      context.commit('saveAcesso', payload)
    },
   saveLogado(context, payload) { 
    context.commit('saveLogado', payload)
    }
  },
  modules: {
  }
})
