import { createStore } from 'vuex'

export default createStore({
  state: {
    token: "",
    acesso: "",
    logado: false
  },
  getters: {
  },
  mutations: { //commit
    saveToken(state, token) {
      state.token = token
      state.logado = true;
    },
    saveAcesso(state, acesso) { 
      state.acesso = acesso
    }

  },
  actions: { //dispatch
    saveToken(context, token) { 
       context.commit('saveToken', token)
    },
    saveAcesso(context, acesso) { 
      context.commit('saveAcesso', acesso)
    }
  },
  modules: {
  }
})
