import { createStore } from 'vuex'

export default createStore({
  state: {
    token: "",
    acesso: "",
    logado: false
  },
  mutations: { //commit

    //setando o token e definindo o logado como true
    saveToken(state, token) {
      state.token = token
      state.logado = true;
    },
    saveAcesso(state, acesso) { 
      state.acesso = acesso
    }
  },
  modules: {
  }
})
